import React, { useMemo } from 'react';
import { useGLTF } from '@react-three/drei';
import * as THREE from 'three';

export function PictureFrame(props) {
    const { nodes, materials } = useGLTF('./models/picture_frame.glb');

    // ðŸ§  Create a glowing clone of the material
    const glowMaterial = useMemo(() => {
        const mat = materials['palette.024'].clone();
        mat.emissive = new THREE.Color('#0f1aff');  // color of the glow
        mat.emissiveIntensity = 2;                  // brightness
        mat.emissiveMap = null;                     // optional
        return mat;
    }, [materials]);

    return (
        <group {...props} dispose={null}>
            <mesh
                castShadow
                receiveShadow
                geometry={nodes.Untitled004.geometry}
                material={glowMaterial}
                rotation={[Math.PI / 2, 0, 0]}
            />
        </group>
    );
}
